import { gql } from "@apollo/client";

export const QUERY_LOGIN_USER = gql`
	query Mobile_UserLogin($username: String!, $password: String!) {
		users(
			where: {
				username: { _eq: $username }
				password: { _eq: $password }
				is_deleted: { _eq: 0 }
			}
		) {
			id
			fullname
			phone
			token
			email
			postals(where: { is_deleted: { _eq: 0 } }) {
				id
				image_url
				name
				location
				region_id
				is_actived
				postal_code
			}
		}
	}
`;

export const QUERY_CHECK_USER_TOKEN = gql`
	query Mobile_CheckUserToken($token: String!) {
		users(where: { token: { _eq: $token } }) {
			id
			fullname
			phone
			token
			email
			postals(where: { is_deleted: { _eq: 0 } }) {
				id
				image_url
				name
				location
				region_id
				is_actived
				postal_code
			}
		}
	}
`;

export const MUTATION_SIGNUP_USER = gql`
	mutation Mobile_SignUpUser(
		$fullname: String!
		$username: String!
		$password: String!
	) {
		insert_postals(
			object: { fullname: $fullname, username: $username, password: $password }
		) {
			id
			fullname
			phone
			token
			email
			postals(where: { is_deleted: { _eq: 0 } }) {
				id
				image_url
				name
				location
				region_id
				is_actived
				postal_code
			}
		}
	}
`;

export const MUTATION_UPDATE_PROFILE_DETAIL = gql`
	mutation Mobile_UpdateProfileDetail(
		$id: Int!
		$address: String!
		$email: String!
		$phone: String!
		$fullname: String!
	) {
		update_users(
			where: { id: { _eq: $id } }
			_set: { fullname: $fullname, email: $email, address: $address }
		) {
			returning {
				id
			}
		}
	}
`;

export const QUERY_GET_ALL_POSTAL_PLACE = gql`
	query Mobile_QueryGetAllPostalPlace {
		postals(where: { is_deleted: { _eq: 0 } }) {
			id
			name
			lng
			lat
			location
			postal_code
			phone
			region_id
			status
			type
			website
			image_url
			email
			geocode
			uid
			created_at
		}
	}
`;

export const QUERY_SEARCH_ALL_POSTAL_PLACE = gql`
	query Mobile_SearchAllPostalPlace($name: String!) {
		postals(where: { _or: { name: { _like: $name } } }) {
			id
			name
			lng
			lat
			location
			postal_code
			phone
			region_id
			status
			type
			website
			image_url
			email
			geocode
			uid
			created_at
		}
	}
`;

export const QUERY_GET_POSTAL_DETAIL = gql`
	query GetPostalDetail($id: Int!) {
		postals(where: { id: { _eq: $id }, is_deleted: { _eq: 0 } }) {
			name
			phone
			postal_code
			region_id
			website
			lat
			lng
			email
		}
	}
`;

export const QUERY_GET_POSTAL_BY_BARCODE = gql`
	query Mobile_GetPostalByBarcode($id: Int!) {
		postals(where: { id: { _eq: $id }, is_deleted: { _eq: 0 } }) {
			id
			name
			phone
			postal_code
			region_id
			website
			lat
			lng
			email
		}
	}
`;

// MY POSTAL
export const QUERY_GET_ALL_MY_POSTAL = gql`
	query Mobie_GetAllMyPostal($uid: Int!) {
		postals(where: { uid: { _eq: $uid }, is_deleted: { _eq: 0 } }) {
			id
			image_url
			is_actived
			is_approved
			is_deleted
			lat
			lng
			name
			phone
			postal_code
			region_id
			status
			type
			email
			created_at
			address
		}
	}
`;

export const MUTATION_EDIT_POSTAL_DETAIL = gql`
	mutation Mobile_EditPostalDetail(
		$id: Int!
		$name: String!
		$image_url: String!
	) {
		update_postals(
			where: { id: { _eq: $id } }
			_set: { name: $name, image_url: $image_url }
		) {
			returning {
				id
			}
		}
	}
`;

// export const MUTATION_UPDATE_USER_PROFILE = gql``

// export const MUTATION_CHANGE_PASSWORD_USER = gql``

export const MUTATION_CREATE_POSTAL = gql`
	mutation Mobile_CreatePostal(
		$name: String!
		$image_url: String!
		$address: String!
		$lat: String!
		$lng: String!
		$type: Int!
		$uid: Int!
	) {
		insert_postals(
			objects: {
				name: $name
				image_url: $image_url
				type: $type
				address: $address
				lat: $lat
				lng: $lng
				uid: $uid
			}
		) {
			returning {
				id
			}
		}
	}
`;

