import React, { useState, useEffect } from "react";
import {
	StyleSheet,
	ScrollView,
	View,
	TouchableWithoutFeedback,
	Keyboard,
	Platform,
	AsyncStorage,
	TouchableOpacity,
	TextInput,
	Alert,
} from "react-native";
import { Text } from "@ui-kitten/components";

import { StatusBar } from "expo-status-bar";
import { connect } from "react-redux";

// custom fonts
import { AppLoading } from "expo";

import { useMutation } from "@apollo/client";
import { MUTATION_SIGNUP_USER } from "../../graphql/query";

import { isEmpty, isMin } from "../../functions/strings";

import appConfigs from "../../config";

const color_blue = "#5458F7";

function SignUpScreen(props) {
	const [
		signUpUser,
		{
			error: errorSignUp,
			called: calledSignUp,
			loading: loadingSignUp,
			data: dataSignUp,
		},
	] = useMutation(MUTATION_SIGNUP_USER, {

		onCompleted: (dataSignUp) => {
			console.log("onCompleted");
			console.log(dataSignUp);
			// saveUserData();
		},
		onError: (errorSignUp) => {
			console.log("onError");
			console.log(errorSignUp);
		},
	});

	const [fullnameInput, setFullnameInput] = useState("");
	const [userInput, setUserInput] = useState("");
	const [passwordInput, setPasswordInput] = useState("");
	const [rePasswordInput, setRePasswordInput] = useState("");
	const [loading, setLoading] = useState(false);

	const saveUserData = async () => {
		if (isEmpty(dataSignUp.users[0]) == true) {
			setLoading(false);
			Alert.alert("Tài khoản mật khẩu của bạn không chính xác");
			// this.firstInput.focus();
			return;
		}

		if (dataSignUp) {
			if (dataSignUp.users[0].id) {
				let user = dataSignUp.users[0];

				console.log(126, user);

				props.storeData(user.token);
				props.storeUserInfo(user);

				try {
					await AsyncStorage.setItem("@token", user.token);
				} catch (e) {
					console.log(e);
				}

				props.navigation.navigate("Explore");
			} else {
			}
		} else {
			console.log("sucess graphql but not right response");
		}
	};

	const onClickSignUp = async () => {
		Keyboard.dismiss();

		if (isMin(userInput, appConfigs.VALIDATE.AUTH.MIN_USERNAME)) {
			Alert.alert("Tên tài khoản từ 6 đến 15 ký tự");
			// this.firstInput.focus();
			return;
		} else if (isMin(passwordInput, appConfigs.VALIDATE.AUTH.MIN_PASSWORD)) {
			Alert.alert("Mật khẩu từ 6 đến 15 ký tự");
			return;
		} else if (isMin(fullnameInput, appConfigs.VALIDATE.PROFILE.MIN_FULLNAME)) {
			Alert.alert("Mật khẩu từ 6 đến 15 ký tự");
			return;
		} else if (passwordInput != rePasswordInput) {
			Alert.alert("Mật khẩu bạn nhập không giống nhau");
		}

		signUpUser({
			variables: {
				fullname: fullnameInput,
				username: userInput,
				password: passwordInput,
			}
		});
	};

	if (props.token) {
		props.navigation.navigate("Explore");
		return <AppLoading />;
	} else {
		return (
			<ScrollView
				showsVerticalScrollIndicator={false}
				style={styles.scrollview}
			>
				<View style={{ ...styles.container }}>
					<TouchableWithoutFeedback
						onPress={Keyboard.dismiss}
						style={{ width: "100%" }}
					>
						<View
							style={{
								flexDirection: "column",
								margin: 50,
								marginTop: -60,
								paddingTop: 220,
							}}
						>
							<Text
								style={{
									fontSize: 22,
									color: "#000",
								}}
							>
								Đăng ký
							</Text>

							<Text style={[styles.textBlackSize14]}>Họ và tên</Text>
							<TextInput
								autoCapitalize="none"
								underlineColorAndroid="#00000000"
								returnKeyType={"next"}
								onSubmitEditing={() => {}}
								style={styles.customEditText}
								clearButtonMode="while-editing"
								enablesReturnKeyAutomatically={true}
								blurOnSubmit={false}
								placeholder={"Nhập tài khoản"}
								// ref={(input) => {}}
								onChange={(e) => setFullnameInput(e)}
							/>

							<Text style={[styles.textBlackSize14]}>Tài khoản</Text>
							<TextInput
								autoCapitalize="none"
								underlineColorAndroid="#00000000"
								returnKeyType={"next"}
								onSubmitEditing={() => {}}
								style={styles.customEditText}
								clearButtonMode="while-editing"
								enablesReturnKeyAutomatically={true}
								blurOnSubmit={false}
								placeholder={"Nhập tài khoản"}
								// ref={(input) => {}}
								onChange={(text) => setUserInput(text)}
							/>

							<Text style={styles.textBlackSize14}>Mật khẩu</Text>

							<TextInput
								autoCapitalize="none"
								secureTextEntry
								underlineColorAndroid="#00000000"
								returnKeyType={"next"}
								onSubmitEditing={() => {}}
								style={styles.customEditText}
								clearButtonMode="while-editing"
								enablesReturnKeyAutomatically={true}
								blurOnSubmit={false}
								placeholder={"Nhập mật khẩu"}
								onChange={(text) => setPasswordInput(text)}
							/>

							<Text style={styles.textBlackSize14}>Nhập lại mật khẩu</Text>

							<TextInput
								autoCapitalize="none"
								secureTextEntry
								underlineColorAndroid="#00000000"
								returnKeyType={"next"}
								onSubmitEditing={() => {}}
								style={styles.customEditText}
								clearButtonMode="while-editing"
								enablesReturnKeyAutomatically={true}
								blurOnSubmit={false}
								placeholder={"Nhập mật khẩu"}
								onChange={(text) => setRePasswordInput(text)}
							/>
							<TouchableOpacity
								style={[styles.forgotContainer, { height: 44, marginTop: 16 }]}
							>
								<Text style={styles.forgotPass}>Quên mật khẩu ?</Text>
							</TouchableOpacity>

							<View style={{ flexDirection: "row", marginTop: 24 }}>
								<TouchableOpacity
									style={styles.buttonBackgroundBlue}
									activeOpacity={0.5}
									onPress={() => onClickSignUp()}
								>
									<View style={{ padding: 10 }}>
										<Text style={[styles.textWhite, { fontSize: 16 }]}>
											Tạo tài khoản
										</Text>
									</View>
								</TouchableOpacity>
							</View>

							<View
								style={{
									flexDirection: "row",
									marginTop: 20,
									height: 40,
								}}
							>
								<TouchableOpacity
									style={styles.buttonBorderBlue}
									activeOpacity={0.5}
									onPress={() => props.navigation.navigate("SignIn")}
								>
									<Text style={styles.textSignUp}>Đăng nhập</Text>
								</TouchableOpacity>
							</View>

							<View
								style={{
									flexDirection: "row",
									marginTop: 20,
									height: 40,
								}}
							>
								<TouchableOpacity
									style={styles.buttonBorderBlue}
									activeOpacity={0.5}
									onPress={() => props.navigation.navigate("Explore")}
								>
									<Text style={styles.textSignUp}>Trang chủ</Text>
								</TouchableOpacity>
							</View>
						</View>
					</TouchableWithoutFeedback>
				</View>
			</ScrollView>
		);
	}
}

