import React from "react";
import { ScrollView, TouchableOpacity, SafeAreaView, View } from "react-native";
import { connect } from "react-redux";
import { useNavigation } from "@react-navigation/native";
import { List, ListItem, Divider } from "@ui-kitten/components";
import Spinner from "react-native-loading-spinner-overlay";


function ListScreen(props) {
	const navigation = useNavigation();


	let renderItem = ({ item, index }) => {

		return (
			<ListItem
				key={item.id}
				title={`${item.name}`}
				description={`${item.postal_code}`}
				onPress={() => navigation.navigate("Postal", { postal: item })}
			/>
		);
	};

	return (
		<SafeAreaView>
			<View style={{backgroundColor: '#fff'}}>
				<Spinner visible={props.loading == 1 ? true : false} />
				<List
					style={{paddingTop: 45}}
					data={props.filteredPlaces}
					ItemSeparatorComponent={Divider}
					renderItem={renderItem}
				/>
			</View>
		</SafeAreaView>
	);

}

function mapStateToProps(state) {
	return { favs: state.favs };
}

export default connect(mapStateToProps, null)(ListScreen);
