import React, { useState, useEffect } from "react";
import {
	ScrollView,
	View,
	SafeAreaView,
	TouchableOpacity,
	Dimensions,
	Alert,
} from "react-native";
import {
	Button,
	Layout,
	StyleService,
	Text,
	Input,
	Icon,
	useStyleSheet,
	TopNavigation,
	TopNavigationAction,
	Divider,
	Avatar,
	RadioGroup,
	Radio,
	Datepicker,
	NativeDateService,
} from "@ui-kitten/components";

// import { ProfileSetting } from "./extra/profile-setting.component";
// import { ProfileAvatar } from "./extra/profile-avatar.component";
// import { CameraIcon } from "./extra/icons";
// import { Profile } from "./extra/data";

import { connect } from "react-redux";

import { MUTATION_UPDATE_PROFILE_DETAIL } from "../../../graphql/query";

import { useMutation } from "@apollo/client";
import { isEmpty, isMin } from "../../../functions/strings";
import Spinner from "react-native-loading-spinner-overlay";

// const profile: Profile = Profile.jenniferGreen();

const BackIcon = (props) => <Icon {...props} name="arrow-back" />;
const CalendarIcon = (props) => <Icon {...props} name="calendar" />;

const useDatepickerState = (initialDate = null) => {
	const [date, setDate] = React.useState(initialDate);
	return { date, onSelect: setDate };
};

const i18n = {
	dayNames: {
		short: ["CN", "T2", "T3", "T4", "T5", "T6", "T7"],
		long: [
			"Chủ nhật",
			"Thứ hai",
			"Thứ ba",
			"Thứ tư",
			"Thứ năm",
			"Thứ sáu",
			"Thử bảy",
		],
	},
	monthNames: {
		short: [
			"T1",
			"T2",
			"T3",
			"T4",
			"T5",
			"T6",
			"T7",
			"T8",
			"T9",
			"T10",
			"T11",
			"T12",
		],
		long: [
			"Tháng một",
			"Tháng hai",
			"Tháng ba",
			"Tháng tư",
			"Tháng năm",
			"Tháng sáu",
			"Tháng bảy",
			"Tháng tám",
			"Tháng chín",
			"Tháng mười",
			"Tháng mười một",
			"Tháng mười hai",
		],
	},
};

const localeDateService = new NativeDateService("ru", {
	i18n,
	startDayOfWeek: 1,
});
const formatDateService = new NativeDateService("en", { format: "DD.MM.YYYY" });

function EditProfileScreen(props) {
	const [
		updateInfo,
		{
			error: errorInfo,
			called: calledInfo,
			loading: loadingInfo,
			data: dataInfo,
		},
	] = useMutation(MUTATION_UPDATE_PROFILE_DETAIL, {
		onCompleted: (dataInfo) => {
			console.log("onCompleted");
			console.log(44, dataInfo);

			setLoading(false);

			if (isEmpty(dataInfo.update_users.returning[0]) == true) {
				console.log("false");

				setTimeout(() => {
					Alert.alert("Có lổi xảy ra. Vui lòng thử lại sau");
				}, 800);
			} else {
				props.storeUserInfo(dataInfo.update_users.returning[0]);

				// props.navigation.navigate("More");
				setTimeout(() => {
					Alert.alert("Đã lưu");
				}, 800);
			}
		},
		onError: (errorInfo) => {
			console.log("onError");
			console.log(errorInfo);
		},
	});

	const [fullnameInput, setFullnameInput] = useState("");
	const [idNationInput, setIdNationInput] = useState("");
	const [genderInput, setGenderInput] = useState("");
	const [birthdayInput, setBirthdayInput] = useState("");
	const [addressInput, setAddressInput] = useState("");

	const [date, setDate] = useState(new Date());
	const dateFormatPickerState = useDatepickerState();
	const localePickerState = useDatepickerState();

	const [dataInfoResponse, setDataInfo] = useState({});
	const [selectedIndex, setSelectedIndex] = useState(null);

	const [loading, setLoading] = useState(false);

	const styles = useStyleSheet(themedStyles);

	// console.log(300, props.token);
	// console.log(311, props.infos);

	useEffect(() => {
		console.log(145, props.infos);

		setFullnameInput(props.infos.fullname);

		setGenderInput(props.infos.gender);
		setSelectedIndex(props.infos.gender - 1);

		setBirthdayInput(props.infos.birthday);
		setAddressInput(props.infos.address);

		const getPostals = async () => {
			var allPostals = await getListPostal();
		};
	}, []);

	let userData;

	function onSelectGender(index) {
		// if (index != 0 && index != 1) return false

		setSelectedIndex(index);
		setGenderInput(index + 1);
	}

	const onClickSubmit = async () => {

		if (!fullnameInput.trim()) {
			Alert.alert("Vui lòng nhập họ tên");
			return;
		} else if (!genderInput.trim()) {
			Alert.alert("Vui lòng chọn giới tính");
			return;
		} else if (!birthdayInput.trim()) {
			Alert.alert("Vui lòng nhập sinh nhật");
			return;
		}

		// console.log(100, "onClickSubmit");

		console.log("gender: " + genderInput);
		console.log("gender: " + birthdayInput);
		setLoading(true);

		let valueGender = genderInput + 1;

		updateInfo({
			variables: {
				id: props.infos.id,
				fullname: fullnameInput,
				gender: genderInput,
				birthday: birthdayInput,
				address: addressInput,
			},
		});
	};

	const renderPhotoButton = () => (
		<Button
			style={styles.photoButton}
			size="small"
			status="basic"
			icon={CameraIcon}
		/>
	);

	const renderBackAction = () => (
		<TopNavigationAction
			icon={BackIcon}
			onPress={() => props.navigation.navigate("More")}
		/>
	);

	return (
		<SafeAreaView style={{ paddingTop: 20 }}>
			<Spinner visible={loadingInfo} />
			<TopNavigation
				alignment="center"
				title="Chỉnh sửa tài khoản"
				accessoryLeft={renderBackAction}
				// accessoryRight={renderRightActions}
			/>
			<Divider />
			{/*<Layout style={styles.photoSection} level="1">
				<ProfileAvatar
					style={styles.photo}
					source={profile.photo}
					editButton={renderPhotoButton}
				/>
				<View style={styles.nameSection}>
					<ProfileSetting
						style={styles.setting}
						value={profile.firstName}
					/>
					<ProfileSetting
						style={styles.setting}
						value={profile.lastName}
					/>
				</View>
			</Layout>*/}
			{/*
			<Text style={styles.description} appearance="hint">
					{profile.description}
				</Text>
			*/}

			<View style={[themedStyles.formContainer]}>
				{/*<Avatar
					style={[styles.profileAvatar, styles.avatar]}
					source={profile.photo}
				/>*/}
				<Input
					placeholder="Nhập họ và tên"
					label="Họ và tên"
					autoCapitalize="words"
					value={fullnameInput}
					onChangeText={(text) => setFullnameInput(text)}
					style={{ paddingBottom: 15 }}
				/>
				<Datepicker
					style={{ paddingBottom: 15 }}
					label="Ngày sinh"
					date={date}
					onSelect={(nextDate) => setDate(nextDate)}
					accessoryRight={CalendarIcon}
					dateService={localeDateService}
					{...localePickerState}
				/>

				<Input
					placeholder=""
					label="Số CMT/CCCD/Hộ chiếu"
					autoCapitalize="words"
					value={idNationInput}
					onChangeText={(text) => setIdNationInput(text)}
					style={{ paddingBottom: 5 }}
					keyboardType="numeric"
				/>
				<Layout style={styles.containerRadio} level="1">
					<RadioGroup
						selectedIndex={selectedIndex}
						onChange={(index) => onSelectGender(index)}
					>
						<Radio value="1">Nam</Radio>
						<Radio value="2">Nữ</Radio>
					</RadioGroup>
				</Layout>

				<Input
					placeholder=""
					label="Địa chỉ"
					autoCapitalize="words"
					value={addressInput}
					onChangeText={(text) => setAddressInput(text)}
					style={{ paddingVertical: 5 }}
				/>

				<Button
					style={styles.doneButton}
					onPress={() => onClickSubmit()}
					status="info"
				>
					Lưu
				</Button>
			</View>
		</SafeAreaView>
	);
}

const themedStyles = StyleService.create({
	container: {
		flex: 1,
		backgroundColor: "background-basic-color-2",
	},
	containerRadio: {
		flexDirection: "row",
		flexWrap: "wrap",
		paddingBottom: 10,
	},
	containerInput: {
		flexDirection: "row",
		justifyContent: "space-between",
		alignItems: "center",
	},
	formContainer: {
		padding: 20,
		backgroundColor: "#fff",
		height: Dimensions.get("window").height,
	},
	contentContainer: {
