import React from "react";
import { Image, View } from "react-native";

import { ApolloProvider, ApolloClient, InMemoryCache } from "@apollo/client";

// Navigation
import { createStackNavigator } from "@react-navigation/stack";
import { NavigationContainer, useNavigation } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import * as Sentry from "sentry-expo";

import { ApplicationProvider, IconRegistry, Layout, Text } from "@ui-kitten/components";
import { EvaIconsPack } from '@ui-kitten/eva-icons';

// icons
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { FontAwesome5 } from "@expo/vector-icons";
import { FontAwesome } from "@expo/vector-icons";
import { Entypo } from "@expo/vector-icons";

// AUTH
import SignInScreen from "./screens/auth/signIn";
import SignUpScreen from "./screens/auth/signUp";


// EXPLORE
import ExploreScreen from "./screens/explore";
import SearchScreen from "./screens/search";
import SearchedResultsScreen from "./screens/searchedResults";
import FilterModalScreen from "./screens/explore/filterModalScreen";


// POSTAL
import MyPostalScreen from "./screens/postal/myPostal";
import CreatePostalScreen from "./screens/postal/createPostal";
import DetailPostalScreen from "./screens/postal/detailPostal";
import ScanQRScreen from "./screens/postal/scanQR";


// ACCOUNT
import ProfileScreen from "./screens/account";


// MORE
import MoreScreen from "./screens/more";
import IntroModalScreen from "./screens/more/intro";
import DocumentModalScreen from "./screens/more/document";
import GuideModalScreen from "./screens/more/guide";

import appConfigs from "./config";

import * as eva from "@eva-design/eva";

// redux
import { Provider } from "react-redux";
import { createStore, combineReducers } from "redux";
import token from "./reducers/token";
import infos from "./reducers/userInfos";
import filter from "./reducers/filter";
import favs from "./reducers/favorites";
const store = createStore(combineReducers({ token, favs, infos, filter }));

// colors vars
var blueDark = "#033C47";
var mintLight = "#1da1f2";

Sentry.init({
	dsn: "https://c647846d2e184328a7ab4db946f7e5b6@o512568.ingest.sentry.io/5626788",
	enableInExpoDevelopment: true,
	debug: true
});

// Sentry.setUser({ mode: "test_for_monday" });


// Sentry.Native.*

// function LogoTitle() {
// 	return (
// 		<View style={{ justifyContent: "center", alignItems: "center" }}>
// 			<Image
// 				style={{ width: 75, height: 26 }}
// 				source={require("./assets/images/logo.png")}
// 			/>
// 		</View>
// 	);
// }

// function FeedAline() {
//   // tip : à l'intérieur d'un header, il faut utiliser useNavigation au lieu de navigate() pour faire un lien
//   // const navigation = useNavigation()
//   return (
//     <Image
//     style={{ width: 34, height: 26 , marginLeft: 22}}
//     source={require('./assets/icons/feedAline.png')}
//   />
//   )
// }

/* #################### Creating a modal stack #################### */
/* use MainStackScreen component as a screen inside RootStackScreen */

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const MainStack = createStackNavigator();
const RootStack = createStackNavigator();

const client = new ApolloClient({
	uri: appConfigs.API.GRAPHQL.URI,
	cache: new InMemoryCache(),
});

function MyTabs() {
	return (
		<Tab.Navigator
			tabBarOptions={{
				activeTintColor: "#fff",
				inactiveTintColor: "#d9d9d9",
				activeBackgroundColor: mintLight,
				inactiveBackgroundColor: mintLight,
				safeAreaInset: { bottom: "never", top: "never" },
				style: {
					backgroundColor: mintLight,
				},
			}}
		>
			<Tab.Screen
				name="Explorer"
				component={ExploreScreen}
				options={{
					tabBarLabel: "Tìm kiếm",
					tabBarIcon: ({ color, size }) => (
						<FontAwesome5 name="search-location" size={24} color={"#fff"} />
					),
				}}
			/>
			<Tab.Screen
				name="Deposit"
				component={SearchScreen}
				options={{
					tabBarLabel: "Quét mã",
					tabBarIcon: ({ color, size }) => (
						<MaterialCommunityIcons
							name="barcode-scan"
							size={24}
							color={"#fff"}
						/>
					),
				}}
			/>
			<Tab.Screen
				name="MyPostaL"
				component={MyPostalScreen}
				options={{
					tabBarLabel: "Mã bưu chính",
					tabBarIcon: ({ color, size }) => (
						<FontAwesome5 name="store" size={24} color={color} />
					)
				}}
			/>
			<Tab.Screen
				name="MoreScreen"
				component={MoreScreen}
				options={{
					tabBarLabel: "Hướng dẫn",
					tabBarIcon: ({ color, size }) => (
						<FontAwesome5 name="question-circle" size={24} color={color} />
					),
				}}
			/>
		</Tab.Navigator>
	);
}

function MainStackScreen() {
	return (
		<MainStack.Navigator>
			<MainStack.Screen
				name="Explore"
				component={MyTabs}
				options={{
					headerShown: false,
					// headerTitle: (props) => <LogoTitle {...props} />,
					// headerLeft: props => <FeedAline {...props}/>,
					// headerLeft: null,
					// headerStyle: {
					// 	backgroundColor: "#1da1f2",
					// },
					// headerTintColor: "#fff",
				}}
			/>
			<MainStack.Screen
				name="SignIn"
				component={SignInScreen}
				options={{ headerShown: false }}
			/>
			<MainStack.Screen
				name="SignUp"
				component={SignUpScreen}
				options={{ headerShown: false }}
			/>
		</MainStack.Navigator>
	);
}

function App() {
	return (
		<ApolloProvider client={client}>
			<Provider store={store}>
				<IconRegistry icons={EvaIconsPack} />
				<ApplicationProvider {...eva} theme={eva.light}>
					<NavigationContainer>
						<RootStack.Navigator mode="modal" headerMode="none">
							<RootStack.Screen name="Main" component={MainStackScreen} />
							<RootStack.Screen name="Postal" component={DetailPostalScreen} />
							<RootStack.Screen name="Filter" component={FilterModalScreen} />
							<RootStack.Screen name="Account" component={ProfileScreen} />
							<RootStack.Screen
								name="CreatePostal"
								component={CreatePostalScreen}
							/>
							<RootStack.Screen
								name="SearchedResults"
								component={SearchedResultsScreen}
							/>
							<RootStack.Screen
								name="IntroModal"
								component={IntroModalScreen}
							/>
							<RootStack.Screen
								name="DocumentModal"
								component={DocumentModalScreen}
							/>
							<RootStack.Screen
								name="GuideModal"
								component={GuideModalScreen}
							/>
							<RootStack.Screen name="ScanQR" component={ScanQRScreen} />
						</RootStack.Navigator>
					</NavigationContainer>
				</ApplicationProvider>
			</Provider>
		</ApolloProvider>
	);
}

// keep this at the end
export default App;
